package Javabasics;

public class loopingconcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//while loop
		int i = 1;
		while(i<=21) {
			System.out.println(i);
			i++;
		}
		//for loop
		
		for(int a=1;a<=15;a++) {
			System.out.println(a);
		}
		for(int j=13;j>=1;j--) {
			System.out.println(j);
			
		}

	}

}
